import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import orderStyles from '../AdminLogs/AdminLogs.module.css';
import SideMenu from '../SideMenu/SideMenu';

const socket = io('http://localhost:5000'); // Flask backend URL

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [orderStatus, setOrderStatus] = useState('');
  const customerId = getUserDataFromCookies();

  useEffect(() => {
    if (customerId) {
      fetch(`/orders/${customerId}`)
        .then((response) => response.json())
        .then((data) => setOrders(data.orders))
        .catch((error) => console.error('Error fetching orders:', error));
    }

    // WebSocket listeners
    socket.on('order_status', (data) => {
      if (data.order_id === selectedOrder) {
        setOrderStatus(data.status);
      }
    });

    socket.on('error', (data) => {
      console.error('WebSocket Error:', data.message);
    });

    return () => {
      socket.disconnect();
    };
  }, [customerId, selectedOrder]);

  const handleTrackOrder = (orderId) => {
    setSelectedOrder(orderId);
    setOrderStatus('Tracking...');
    socket.emit('track_order', { order_id: orderId });
  };

  return (
    <div className='my-orders'>
      <h1>THREADYOL</h1>
      <div className="sidebar">
        <SideMenu />
      </div>
      <div className={orderStyles.container}>
        <h1>My Orders</h1>
        {orders.length > 0 ? (
          <table className={orderStyles.table}>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Product ID</th>
                <th>Quantity</th>
                <th>Total Price</th>
                <th>Order Date</th>
                <th>Order Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.OrderID}>
                  <td>{order.OrderID}</td>
                  <td>{order.ProductID}</td>
                  <td>{order.Quantity}</td>
                  <td>{order.TotalPrice} TL</td>
                  <td>{order.OrderDate}</td>
                  <td>{selectedOrder === order.OrderID ? orderStatus : order.OrderStatus}</td>
                  <td>
                    <button onClick={() => handleTrackOrder(order.OrderID)}>Track</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
};

export default MyOrders;
