import React, { useEffect, useState } from 'react';
import AdminSideMenu from '../AdminSideMenu/AdminSideMenu';
import Modal from 'react-modal'; // Modal için bir kütüphane
import styles from './AdminAllCustomers.module.css';

Modal.setAppElement('#root'); // Modal erişilebilirlik ayarı

const AdminAllCustomers = () => {
  const [customers, setCustomers] = useState([]);
  const [selectedCustomerLogs, setSelectedCustomerLogs] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  // Müşterileri getir
  const fetchCustomers = async () => {
    try {
      const response = await fetch('/all-customers');
      if (response.ok) {
        const data = await response.json();
        setCustomers(data.customers);
      } else {
        console.error('Müşteriler alınamadı:', response.statusText);
      }
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  // Seçili müşterinin loglarını getir
  const fetchCustomerLogs = async (customerId) => {
    try {
      const response = await fetch(`/customer-logs/${customerId}`);
      if (response.ok) {
        const data = await response.json();
        setSelectedCustomerLogs(data.logs);
        setIsModalOpen(true); // Modalı aç
      } else {
        console.error('Loglar alınamadı:', response.statusText);
      }
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  // Müşteri seçildiğinde logları yükle
  const handleCustomerClick = (customer) => {
    setSelectedCustomer(customer);
    fetchCustomerLogs(customer.id);
  };

  useEffect(() => {
    fetchCustomers();
  }, []);

  return (
    <div className="admin-all-users">
      <h1>THREADYOL - Müşteri Listesi</h1>
      <div className="sidebar">
        <AdminSideMenu />
      </div>
      <div className="all-customers">
        <div className={styles.container}>
          {Array.isArray(customers) && customers.length > 0 ? (
            customers.map((customer) => (
              <div
                key={customer.id}
                className={styles.card}
                onClick={() => handleCustomerClick(customer)}
              >
                <h3>
                  {customer.name} {customer.surname}
                </h3>
                <p>Telefon: {customer.phone_number}</p>
                <p>Bütçe: {customer.budget} TL</p>
                <p>Tür: {customer.customer_type}</p>
                <p>Toplam Harcama: {customer.total_spent} TL</p>
              </div>
            ))
          ) : (
            <p>No users found.</p>
          )}
        </div>
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        className={styles.modal}
        overlayClassName={styles.overlay}
      >
        <h2>
          {selectedCustomer?.name} {selectedCustomer?.surname} - Loglar
        </h2>
        <button onClick={() => setIsModalOpen(false)}>Kapat</button>
        <div className={styles.logList}>
          {selectedCustomerLogs.length > 0 ? (
            selectedCustomerLogs.map((log) => (
              <div key={log.LogID} className={styles.logItem}>
                <p><strong>Tarih:</strong> {log.LogDate}</p>
                <p><strong>Tip:</strong> {log.LogType}</p>
                <p><strong>Detay:</strong> {log.LogDetails}</p>
              </div>
            ))
          ) : (
            <p>Bu müşteri için log bulunamadı.</p>
          )}
        </div>
      </Modal>
    </div>
  );
};

export default AdminAllCustomers;
