import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import styles from './AdminDynamicLog.module.css';

const AdminDynamicLog = () => {
  const [logs, setLogs] = useState([]);
  
  useEffect(() => {
    const socket = io('http://127.0.0.1:5000');  // Connect to Flask-SocketIO server

    socket.on('connect', () => {
      console.log('WebSocket bağlantısı kuruldu.');
    });

    // Listen for the initial logs from the server (when first connected)
    socket.on('dynamic_logs', (receivedLogs) => {
      console.log('Gelen loglar:', receivedLogs); // Log the received logs to the console

      if (Array.isArray(receivedLogs)) {
        setLogs(receivedLogs); // If we receive an array of logs, set them as initial logs
      } else {
        setLogs((prevLogs) => [receivedLogs, ...prevLogs]); // Add a new log if a single log is received
      }
    });

    socket.on('disconnect', () => {
      console.log('WebSocket bağlantısı kapatıldı.');
    });

    return () => {
      socket.disconnect(); // Clean up when the component unmounts
    };
  }, []);

  return (
    <div className={styles.logContainer}>
      <h2>Dinamik Loglar</h2>
      <ul className={styles.logList}>
        {logs.map((log, index) => (
          <li key={index} className={styles.logItem}>
            <strong>{log.LogDate}</strong> - {log.LogType}: Müşteri {log.CustomerID} {log.LogDetails}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDynamicLog;
