import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginForm from './Components/LoginForm/LoginForm';
import MainMenu from './Components/MainMenu/MainMenu';


function App() {


  useEffect(() => {
    const handleTabClose = () => {
      const rememberMe = document.cookie
        .split('; ')
        .find((row) => row.startsWith('remember_me='))
        ?.split('=')[1];

      if (rememberMe !== 'true') {
        document.cookie = 'user_data=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
      }
    };

    window.addEventListener('beforeunload', handleTabClose);

    return () => {
      window.removeEventListener('beforeunload', handleTabClose);
    };

  }, []);




  return (
    <Router>
    <Routes>
      <Route path="/" element={<Navigate to="/login" replace />} />
      <Route path="/login" element={<LoginForm />} />
      <Route path="/register" element={<RegisterForm />} />

      {/* AuthGuard ile korunan alanlar */}
      <Route
        path="/*"
        element={
          <AuthGuard>
            <Routes>

            <Route path="/mainmenu" element={<MainMenu />} />
              /*
              <Route path="/admin-verify-events" element={<AdminVerifyEvents />} />
              <Route path="/admin-all-users" element={<AdminAllUsers />} />
              <Route path="/admin-mainmenu" element={<AdminAllEvents />} />

              <Route path="/create-event" element={<CreateEvent />} />
              <Route path="/all-events" element={<AllEvents />} />
              <Route path="/my-events" element={<MyEvents />} />
              <Route path="/chats" element={<Chats />} />
              <Route path="/view-profile" element={<ViewMyProfile />} />
              <Route path="/logout" element={<LogOut />} />*

            </Routes>
          </AuthGuard>
        }
      />
    </Routes>
  </Router>
  );
}

export default App;
