import React, { useEffect, useState } from 'react';
import styles from './Logs.module.css';

const Logs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchLogs = async () => {
    try {
      const response = await fetch('/all-logs');
      if (response.ok) {
        const data = await response.json();
        setLogs(data.logs);
      } else {
        console.error('Veriler alınamadı:', response.statusText);
      }
    } catch (error) {
      console.error('Hata oluştu:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  return (
    <div className={styles.container}>
      <h1>Log Listesi</h1>
      {loading ? (
        <p>Yükleniyor...</p>
      ) : logs.length > 0 ? (
        <table className={styles.table}>
          <thead>
            <tr>
              <th>Log ID</th>
              <th>Müşteri ID</th>
              <th>Sipariş ID</th>
              <th>Tarih</th>
              <th>Saat</th>
              <th>Tür</th>
              <th>Detay</th>
            </tr>
          </thead>
          <tbody>
            {logs.map((log) => (
              <tr key={log.LogID}>
                <td>{log.LogID}</td>
                <td>{log.CustomerID}</td>
                <td>{log.OrderID}</td>
                <td>{log.LogDate}</td>
                <td>{log.LogTime}</td>
                <td>{log.LogType}</td>
                <td>{log.LogDetails}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Log bulunamadı.</p>
      )}
    </div>
  );
};

export default Logs;
