import React, { useState, useEffect } from 'react';
import AdminSideMenu from '../AdminSideMenu/AdminSideMenu';
import PendingOrders from '../PendingOrders/PendingOrders';
import pendingStyles from './AdminMainMenu.module.css'

export const AdminMainMenu = () => {
  const [orders, setOrders] = useState([]);

  const fetchPendingOrders = async () => {
    try {
      const response = await fetch('/get_pending_orders');
      if (response.ok) {
        const data = await response.json();
        setOrders(data.orders);
      } else {
        console.error('Veriler alınamadı:', response.statusText);
      }
    } catch (error) {
      console.error('Hata oluştu:', error);
    }
  };

  useEffect(() => {
    fetchPendingOrders();
  }, []);

  return (
    <div className="adminmain-menu">
      <h1>THREADYOL</h1>
      <div className="sidebar">
        <AdminSideMenu />
      </div>
      <div className="orders">
        <div className={pendingStyles.container}>
        {orders.length > 0 ? (
          orders.map((order) => (
            <div key={order.OrderID} className={pendingStyles.card}>
              <h3>Sipariş ID: {order.OrderID}</h3>
              <p>Müşteri: {order.CustomerName}</p>
              <p>Ürün ID: {order.ProductID}</p>
              <p>Adet: {order.Quantity}</p>
              <p>Toplam Fiyat: {order.TotalPrice} TL</p>
              <p>Tarih: {order.OrderDate}</p>
              <p>Durum: {order.OrderStatus}</p>
            </div>
          ))
        ) : (
          <p>Beklemede sipariş bulunamadı.</p>
        )}
      </div>
      </div>
    </div>
  );
};

export default AdminMainMenu;
