import React, { useState, useEffect } from 'react';
import styles from './Urunler.module.css';

export const Urunler = () => {
  // Ürün verileri için state
  const [urunler, setUrunler] = useState([]);

  // Verileri database'den çekme fonksiyonu
  const fetchUrunler = async () => {
    try {
      const response = await fetch('/api/urunler'); // API endpoint veya veri servisi
      const data = await response.json();
      setUrunler(data);
    } catch (error) {
      console.error('Veriler alınamadı:', error);
    }
  };

  useEffect(() => {
    fetchUrunler();
  }, []);

  // Ürün tıklandığında yapılacak işlem
  const handleClick = (urun) => {
    alert(`${urun.isim} tıklandı!`);
  };

  return (
    <div className={styles.container}>
      {urunler.map((urun) => (
        <div
          key={urun.id}
          className={styles.card}
          onClick={() => handleClick(urun)}
        >
          <h3 className={styles.name}>{urun.isim}</h3>
          <p className={styles.price}>{urun.fiyat}</p>
        </div>
      ))}
    </div>
  );
};

export default Urunler;
