import React, { useState, useEffect } from 'react';
import styles from './AdminAllProducts.module.css';
import AdminSideMenu from '../AdminSideMenu/AdminSideMenu'

const getUserDataFromCookies = () => {
  const cookies = document.cookie.split('; ');
  for (let cookie of cookies) {
    const [key, value] = cookie.split('=');
    if (key === 'user_data') {
      return value;
    }
  }
  return null;
};

export const Urunler = () => {
  const [urunler, setUrunler] = useState([]);
  const [quantities, setQuantities] = useState({});

  const fetchUrunler = async () => {
    try {
      const response = await fetch('/tumurunler');
      if (response.ok) {
        const data = await response.json();
        setUrunler(data.products);
        const initialQuantities = data.products.reduce((acc, product) => {
          acc[product.id] = 1;
          return acc;
        }, {});
        setQuantities(initialQuantities);
      } else {
        console.error('Veriler alınamadı:', response.statusText);
      }
    } catch (error) {
      console.error('Veriler alınamadı:', error);
    }
  };

  const handleDeleteProduct = async (productName) => {
    try {
      const response = await fetch('/urunsil', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: productName }),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Ürünü listeden silmek için state güncellemesi yapabilirsiniz
        setUrunler((prevUrunler) => prevUrunler.filter((product) => product.name !== productName));
      } else {
        alert(data.error);
      }
    } catch (error) {
      console.error('Ürün silinemedi:', error);
    }
  };

  useEffect(() => {
    fetchUrunler();
  }, []);

  return (
    <div className='admin-all-products'>
      <h1>THREADYOL</h1>
      <div className="sidebar">
        <AdminSideMenu />
      </div>
      <div className='products'>
        <div className={styles.container}>
          {Array.isArray(urunler) && urunler.length > 0 ? (
            urunler.map((product) => (
              <div
                key={product.id}
                className={styles.card}
              >
                <h3 className={styles.name}>{product.name}</h3>
                <p className={styles.price}>{product.price} TL</p>
                <p className={styles.price}>{product.stock} Adet</p>

                {/* Silme butonu */}
                <div
                  className={styles.deleteIcon}
                  onClick={() => handleDeleteProduct(product.name)}
                >
                  &#128465; {/* Çöp kutusu simgesi */}
                </div>
              </div>
            ))
          ) : (
            <p>No products found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Urunler;
