// AdminUrunEkle.js
import React from 'react';
import AdminSideMenu from '../AdminSideMenu/AdminSideMenu';
import styles from './AdminUrunEkle.module.css';

export const AdminUrunEkle = () => {

  const [productName, setProductName] = useState('');
  const [productStock, setProductStock] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/urunekle', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: productName,
          stock: parseInt(productStock),
          price: parseInt(productPrice),
        }),
      });

      const data = await response.json();
      if (response.ok) {
        setMessage(`Ürün başarıyla eklendi: ${data.product.name}`);
        setProductName('');
        setProductStock('');
        setProductPrice('');
      } else {
        setMessage(`Hata: ${data.error}`);
      }
    } catch (error) {
      setMessage(`Bağlantı hatası: ${error.message}`);
    }
  };






  return (
    <div className={styles.wrapper}>
      <AdminSideMenu />
      <div className={styles.container}>
        <h1 className={styles.title}>Ürün Ekle</h1>
        <form className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="productName">Ürün İsmi:</label>
            <input
              type="text"
              id="productName"
              name="productName"
              placeholder="Ürün ismini girin"
              className={styles.input}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="productPrice">Ücret:</label>
            <input
              type="number"
              id="productPrice"
              name="productPrice"
              placeholder="Ürünün ücretini girin"
              className={styles.input}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="productStock">Stok:</label>
            <input
              type="number"
              id="productStock"
              name="productStock"
              placeholder="Ürün stok miktarını girin"
              className={styles.input}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="productImage">Resim Yükle:</label>
            <input
              type="file"
              id="productImage"
              name="productImage"
              className={styles.input}
            />
          </div>
          <button type="submit" className={styles.submitButton}>Ekle</button>
        </form>
      </div>
    </div>
  );
};

export default AdminUrunEkle;
