import React, { useEffect, useState } from 'react';
import styles from './AdminDynamicLog.module.css';

const AdminDynamicLog = () => {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const socket = new WebSocket('ws://127.0.0.1:5000/logs');  // Using WebSocket connection

    socket.onopen = () => {
      console.log('WebSocket bağlantısı kuruldu.');
    };

    socket.onmessage = (event) => {
      try {
        const newLog = JSON.parse(event.data);
        setLogs((prevLogs) => [newLog, ...prevLogs]); // Add new log to the top
      } catch (error) {
        console.error('WebSocket mesajı çözülemedi:', error);
      }
    };

    socket.onclose = () => {
      console.log('WebSocket bağlantısı kapatıldı.');
    };

    return () => {
      socket.close();
    };
  }, []);

  return (
    <div className={styles.logContainer}>
      <h2>Dinamik Loglar</h2>
      <ul className={styles.logList}>
        {logs.map((log, index) => (
          <li key={index} className={styles.logItem}>
            <strong>{log.LogDate}</strong> - {log.LogType}: {log.LogDetails}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDynamicLog;
