import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import orderStyles from '../AdminLogs/AdminLogs.module.css';
import SideMenu from '../SideMenu/SideMenu';
import Modal from '../Modal/Modal';

const socket = io('http://localhost:3000'); // Flask backend URL

const getUserDataFromCookies = () => {
  const cookies = document.cookie.split('; ');
  for (let cookie of cookies) {
    const [key, value] = cookie.split('=');
    if (key === 'user_data') {
      return JSON.parse(decodeURIComponent(value));
    }
  }
  return null;
};

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [orderStatus, setOrderStatus] = useState({ order_id: null, status: '', progress: 0 });
  const customerId = getUserDataFromCookies();
  const [modalOpen, setModalOpen] = useState(false);



  const handleTrackOrder = (orderId) => {
    setSelectedOrder(orderId);
    setOrderStatus({ order_id: orderId, status: 'Tracking...', progress: 0 });
    setModalOpen(true);
    socket.emit('track_order', { order_id: orderId });
  };

  const closeModal = () => setModalOpen(false);

  return (
    <div className='my-orders'>
      <h1>THREADYOL</h1>
      <div className="sidebar">
        <SideMenu />
      </div>
      <div className={orderStyles.container}>
        <h1>My Orders</h1>
        {orders.length > 0 ? (
          <table className={orderStyles.table}>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Product ID</th>
                <th>Quantity</th>
                <th>Total Price</th>
                <th>Order Date</th>
                <th>Order Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.OrderID}>
                  <td>{order.OrderID}</td>
                  <td>{order.ProductID}</td>
                  <td>{order.Quantity}</td>
                  <td>{order.TotalPrice} TL</td>
                  <td>{order.OrderDate}</td>
                  <td>{selectedOrder === order.OrderID ? orderStatus.status : order.OrderStatus}</td>
                  <td>
                    <button onClick={() => handleTrackOrder(order.OrderID)}>Track</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
      <Modal
        isOpen={modalOpen}
        onClose={closeModal}
        orderStatus={orderStatus}
      />
    </div>
  );
};

export default MyOrders;
