import React from 'react';
import styles from './Urunler.module.css';

export const Urunler = () => {
  // Örnek ürün verileri
  const urunler = [
    { id: 1, isim: 'Ürün 1', fiyat: '₺50',resim:'ımage1.'},
    { id: 2, isim: 'Ürün 2', fiyat: '₺75' },
    { id: 3, isim: 'Ürün 3', fiyat: '₺100' },
  ];

  const handleClick = (urun) => {
    alert(`${urun.isim} tıklandı!`);
  };

  return (
    <div className={styles.container}>
      {urunler.map((urun) => (
        <div
          key={urun.id}
          className={styles.card}
          onClick={() => handleClick(urun)}
        >
          <img src={urun.resim} alt={urun.isim} className={styles.image} />
          <h3 className={styles.name}>{urun.isim}</h3>
          <p className={styles.price}>{urun.fiyat}</p>
        </div>
      ))}
    </div>
  );
};
export default Urunler;
